import React, {Component} from "react";
import Slider from "react-slick";


function SampleNextArrow(props) {
    const { className, style, onClick } = props;
    return (
        <div
            >
            <i className="fa-solid fa-circle-arrow-right" onClick={onClick}></i>
        </div>

    );
}

function SamplePrevArrow(props) {
    const { className, style, onClick } = props;
    return (
        <div
            className={className}
            style={{ ...style, display: "block", background: "gray" }}
            onClick={onClick}
        />
    );
}
export default class PauseOnHover extends Component {
    render() {
        var settings = {
            infinite: true,
            slidesToShow: 3,
            slidesToScroll: 1,
            autoplay: true,
            autoplaySpeed: 2000,
            pauseOnHover: true,
            nextArrow: <SampleNextArrow />,
            prevArrow: <SamplePrevArrow />
        };
        return (
            <div>
                {/*https://tea-3.lozi.vn/v1/images/resized/banner-mobile-4899-1679481807?w=600&type=o*/}
                <Slider {...settings}>
                    <div>
                        <img src="https://tea-3.lozi.vn/v1/images/resized/banner-mobile-4899-1679481807?w=600&type=o"
                             alt=""/>
                    </div>
                    <div>
                        <img src="https://tea-3.lozi.vn/v1/images/resized/banner-mobile-4899-1679481807?w=600&type=o"
                             alt=""/>
                    </div>
                    <div>
                        <img src="https://tea-3.lozi.vn/v1/images/resized/banner-mobile-4899-1679481807?w=600&type=o"
                             alt=""/>
                    </div>
                    <div>
                        <img src="https://tea-3.lozi.vn/v1/images/resized/banner-mobile-4899-1679481807?w=600&type=o"
                             alt=""/>
                    </div>
                    <div>
                        <img src="https://tea-3.lozi.vn/v1/images/resized/banner-mobile-4899-1679481807?w=600&type=o"
                             alt=""/>
                    </div>
                    <div>
                        <img src="https://tea-3.lozi.vn/v1/images/resized/banner-mobile-4899-1679481807?w=600&type=o"
                             alt=""/>
                    </div>
                </Slider>
            </div>
        );
    }
}
