import {useRef, useState} from "react";
import {Field, Form, Formik} from "formik";
import {saveProduct} from "../service/PruductService";

export default function CreateProduct() {
    const [file, setFile] = useState(null)
    const inputFile = useRef()
    const [product, setProduct] = useState({
        name: "",
        price: "",
        image: "",
        category: [],
        timeMake: ""
    })

    const handledCreate = (e) => {
        console.log(e)
    }
    const handledClickInput=()=>{
        inputFile.current.click();
    }


    return (
        <>
            <div>
                <h1>Create Product</h1>
                <Formik onSubmit={(e) => handledCreate(e)}
                        initialValues={product}>
                    <Form>
                        <div className="input-group mb-3">
                            <span className="input-group-text" id="name">Name</span>
                            <Field type="text" className="form-control" name="name" placeholder="Enter name product"
                                   aria-describedby="name"/>
                        </div>
                        <div className="input-group mb-3">
                            <span className="input-group-text" id="price">Price</span>
                            <Field type="text" className="form-control" name="price" placeholder="Enter price product"
                                   aria-describedby="price"/>
                        </div>
                        <div className="input-group mb-3">
                            <span className="input-group-text" id="timeMake">Time Make</span>
                            <Field type="text" className="form-control" name="timeMake" placeholder="Enter time make product"
                                   aria-describedby="timeMake"/>
                        </div>
                        <div className="mb-3" onClick={handledClickInput}>
                            <label htmlFor="formFile" className="form-label">Image Product</label>
                            <input ref={inputFile} className="form-control" type="file" id="formFile" style={{display : 'none'}} onChange={e=>handledInputFile(e.target.files[0])}/>
                            {file === null? (
                                <div>
                                    <img style={{width:'200px', height:'150px'}}  src={URL.createObjectURL(file)} alt='image' />
                                </div>
                            ):(
                                <div>

                                </div>
                            )}
                        </div>
                        <button type={"submit"}>Save</button>
                    </Form>
                </Formik>
            </div>
        </>
    )
}
