import React, { Component } from "react";
import Slider from "react-slick";

export default class PauseOnHover extends Component {
    render() {
        var settings = {
            infinite: true,
            slidesToShow: 3,
            slidesToScroll: 1,
            autoplay: true,
            autoplaySpeed: 2000,
            pauseOnHover: true
        };
        return (
            <div>
                <h2>Pause On Hover</h2>
                <Slider {...settings}>
                    <div>
                        <div style={{backgroundImage : `url("https://tea-3.lozi.vn/v1/images/resized/banner-mobile-2736-1655806558?w=600&type=o")`}}></div>
                    </div>
                    <div>
                        <h3>2</h3>
                    </div>
                    <div>
                        <h3>3</h3>
                    </div>
                    <div>
                        <h3>4</h3>
                    </div>
                    <div>
                        <h3>5</h3>
                    </div>
                    <div>
                        <h3>6</h3>
                    </div>
                </Slider>
            </div>
        );
    }
}
